{
    "projectName": "IBudget",
    "projectTime": 10,
    "budget": 10.00,
    "fixedCost": 10.00,
    "variableCost": 10.00,
    "categoryId": "a8ab5e7a-6f0d-4538-a65f-5690df90757c",
